from dataclasses import dataclassfrom aiogram.dispatcher.filters import BoundFilterfrom aiogram.dispatcher.handler import ctx_datafrom app import config@dataclassclass IsAdminFilter(BoundFilter):    key = "is_admin"    is_admin: bool    async def check(self, obj):        data = ctx_data.get()        u = data["user"]        return (u.id in config.admins_list) == self.is_admin