# partially from https://github.com/aiogram/botfrom aiogram.dispatcher.middlewares import BaseMiddlewarefrom app.models.db.chat import Chatfrom app.models.db.user import Userfrom app.utils.lock_factory import LockFactoryclass ACLMiddleware(BaseMiddleware):    def __init__(self):        super(ACLMiddleware, self).__init__()        self.lock_factory = LockFactory()    async def setup_chat(self, data, user, chat=None):        async with self.lock_factory.get_lock(f"{user.id}"):            user = await User.get_or_create_from_tg_user(user)        async with self.lock_factory.get_lock(f"{chat.id}"):            chat = await Chat.get_or_create_from_tg_chat(chat)        data["user"] = user        data["chat"] = chat    async def on_pre_process_message(self, message, data):        await self.setup_chat(data, message.from_user, message.chat)    async def on_pre_process_callback_query(self, query, data):        await self.setup_chat(data, query.from_user, query.message.chat if query.message else None)